import React, { Component } from 'react';
import { Form, FormGroup, Col, FormControl, Button, ControlLabel } from 'react-bootstrap';
import AuthService from '../services'

class Sign_In extends Component {
  constructor(props){
    super(props)
    this.auth = new AuthService()
    this.state = {
      form: {
        user: {
          email: "",
          password: "",
        }
      }
    }
  }
  render() {
    let { email, password } = this.state.form.user
      return (
          <div>
            <h1 className="greeting">
              Sign In:
            </h1>
            <Form horizontal onSubmit={this.onSubmit}>
              <FormGroup controlId="formHorizontalEmail">
                <Col componentClass={ControlLabel} sm={2}>
                  Email:
                </Col>
                <Col sm={10}>
                  <FormControl onChange={this.onChange} name="email" value={email} type="email" placeholder="Email" />
                </Col>
              </FormGroup>

              <FormGroup controlId="formHorizontalPassword">
                <Col componentClass={ControlLabel} sm={2}>
                  Password:
                </Col>
                <Col sm={10}>
                  <FormControl onChange={this.onChange} name="password" value={password} type="password" placeholder="Password" />
                </Col>
              </FormGroup>

              <FormGroup>
                <Col smOffset={2} sm={10}>
                  <Button type="submit">Sign In</Button>
                </Col>
              </FormGroup>
            </Form>
          </div>
      )
  }

  // componentDidMount() {
  //   this.props.refresh()
  // }
    onChange = (e) => {
    let { form } = this.state
    form.user[e.target.name] = e.target.value
    this.setState ({
      form
    })
  }
    onSubmit = (e) => {
    e.preventDefault()
    console.log(this.state.form);
    this.auth.sign_in(this.state.form)
    .then(json => {
      // console.log("got to second then:", json)
      if(json.errors) {
        console.log("!! ERRORS !!", json.errors);
        this.setState({
          errors: json.errors
        })
      } else {
        this.props.refresh()
      }
    })
  }
}

export default Sign_In;
